const fadeInElements=document.querySelectorAll(".fadein, .slidefromleft, .slidefromright, .slidefrombottom"),elementInView=e=>e.getBoundingClientRect().top<=(window.innerHeight||document.documentElement.clientHeight)-20,displayScrollElement=e=>{e.classList.add("scrolled")},handleScrollAnimation=()=>{fadeInElements.forEach((e=>{elementInView(e)&&e.classList.add("scrolled")}))};window.addEventListener("scroll",(()=>{fadeInElements.forEach((e=>{elementInView(e)&&e.classList.add("scrolled")}))}));